// HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import CTAButton from './CTAButton';

const HeroSection = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: [0.22, 1, 0.36, 1]
      }
    }
  };

  return (
    <>
      <style>{`
        :root {
          --color-dark-blue-gradient: linear-gradient(135deg, #0d0d0d, #1c1c1c);
          --color-white: #ffffff;
          --border-radius-large: 28px;
          --spacing-unit: 8px;
        }

        .hero-section {
          background: var(--color-dark-blue-gradient);
          color: var(--color-white);
          width: 100%;
          min-height: 60vh;
          border-radius: var(--border-radius-large);
          padding: calc(15 * var(--spacing-unit)) calc(6 * var(--spacing-unit));
          margin-top: calc(5 * var(--spacing-unit));
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          text-align: center;
          position: relative;
          z-index: 1;
          overflow: hidden;
        }

        .hero-section::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: radial-gradient(circle at 20% 50%, rgba(255, 193, 7, 0.08) 0%, transparent 50%),
                      radial-gradient(circle at 80% 30%, rgba(33, 150, 243, 0.06) 0%, transparent 50%);
          pointer-events: none;
          z-index: 0;
        }

        .hero-content {
          position: relative;
          z-index: 1;
          max-width: 1000px;
        }

        .hero-title {
          font-size: 3.5rem;
          font-weight: 900;
          line-height: 1.1;
          margin-bottom: calc(4 * var(--spacing-unit));
          max-width: 800px;
          color: var(--color-white);
          margin-left: auto;
          margin-right: auto;
          letter-spacing: -0.02em;
        }

        .hero-subtitle {
          font-size: 1.25rem;
          opacity: 0.9;
          margin-bottom: calc(8 * var(--spacing-unit));
          max-width: 600px;
          color: var(--color-white);
          margin-left: auto;
          margin-right: auto;
          line-height: 1.6;
        }

        .hero-actions {
          display: flex;
          gap: calc(3 * var(--spacing-unit));
          justify-content: center;
          width: 100%;
          flex-wrap: wrap;
        }

        .city-accent {
          background: linear-gradient(135deg, #FFC107 0%, #FF9800 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        @media (max-width: 900px) {
          .hero-title {
            font-size: 2.5rem;
          }
          .hero-subtitle {
            font-size: 1.1rem;
          }
        }

        @media (max-width: 600px) {
          .hero-actions {
            flex-direction: column;
            gap: 16px;
            align-items: center;
          }
          .hero-title {
            font-size: 2rem;
          }
        }
      `}</style>

      <motion.section 
        className="hero-section"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        <div className="hero-content">
          <motion.h1 className="hero-title" variants={itemVariants}>
            Welcome to <span className="city-accent">NYC</span>: The City That Never Sleeps
          </motion.h1>

          <motion.p className="hero-subtitle" variants={itemVariants}>
            Real-time stories, videos, alerts, and insights across all five boroughs â€” all in one place.
          </motion.p>

          <motion.div 
            className="hero-actions"
            variants={itemVariants}
          >
            <CTAButton 
              label="Explore Now" 
              variant="primary"
              onClick={() => console.log('Hero CTA: Explore Now')}
            />
            <CTAButton 
              label="View Latest" 
              variant="outline-white" 
              onClick={() => console.log('Hero CTA: View Latest')}
            />
          </motion.div>
        </div>
      </motion.section>
    </>
  );
};

export default HeroSection;
