// src/components/HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import CTAButton from './CTAButton';
import heroVideo from '../assets/Hero-Section.mp4';
import '../styles/HeroSection.css'; 

const HeroSection = () => {
  const videoRef = React.useRef(null);

  // Ensure smooth looping without stuttering
  React.useEffect(() => {
    const video = videoRef.current;
    if (video) {
      video.playbackRate = 1.0;

      // Handle video end to create seamless loop
      const handleVideoEnd = () => {
        video.currentTime = 0;
        video.play().catch(err => console.log('Video play error:', err));
      };

      video.addEventListener('ended', handleVideoEnd);

      return () => {
        video.removeEventListener('ended', handleVideoEnd);
      };
    }
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: [0.22, 1, 0.36, 1]
      }
    }
  };

  return (
    <motion.section 
      className="hero-section"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      {/* Background Video */}
      <video 
        ref={videoRef}
        className="hero-video"
        autoPlay 
        loop 
        muted 
        playsInline
        preload="auto"
      >
        <source src={heroVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Video Overlay */}
      <div className="hero-video-overlay"></div>

      <div className="hero-content">
        <motion.h1 className="hero-title" variants={itemVariants}>
          Welcome to <span className="city-accent">NYC</span>: The City That Never Sleeps
        </motion.h1>

        <motion.p className="hero-subtitle" variants={itemVariants}>
          Real-time stories, videos, alerts, and insights across all five boroughs â€” all in one place.
        </motion.p>

        <motion.div 
          className="hero-actions"
          variants={itemVariants}
        >
          <CTAButton 
            label="Explore Now" 
            variant="primary"
            onClick={() => console.log('Hero CTA: Explore Now')}
          />
          <CTAButton 
            label="View Latest" 
            variant="outline-white" 
            onClick={() => console.log('Hero CTA: View Latest')}
          />
        </motion.div>
      </div>
    </motion.section>
  );
};

export default HeroSection;
