// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import Header from './components/Header.jsx';
import HeroSection from './components/HeroSection.jsx'; 
import ContentFeed from './components/ContentFeed.jsx'; 
import SearchBar from './components/SearchBar.jsx'; 
import CTABanner from './components/CTABanner.jsx'; 
import Footer from './components/Footer.jsx'; 
import FeaturesSection from './components/FeaturesSection.jsx'; 

/**
 * @component
 * @description The main application wrapper, defining the global layout and routing structure.
 */
function App() {
  return (
    <Router>
      <div className="App">
        {/* 1.1. Header.jsx: Sticky and animated header */}
        <Header /> 

        <main> {/* FIX 2: Global padding for this tag is now in App.css */}
          <Routes>
            <Route path="/" element={
              // We wrap content in page-container for consistent max-width and centering
              <div className="page-container"> 

                {/* 2.1. HeroSection.jsx - The headline component for the Landing Page */}
                <HeroSection /> 

                {/* 7.1. FeaturesSection.jsx - Why you should use the site */}
                <FeaturesSection /> 

                {/* 4.1. SearchBar.jsx - The search input and quick filters */}
                <SearchBar />

                <h2 style={{ marginTop: '20px', color: 'var(--color-navy-blue)' }}>
                  Latest NYC Headlines
                </h2>

                {/* 3.1. ContentFeed.jsx - The main grid of news cards */}
                <ContentFeed /> 

                {/* 6.1. CTABanner.jsx - Prominent sign-up area */}
                <CTABanner /> 

              </div>
            } />
            {/* Add more routes here if needed */}
          </Routes>
        </main>

        {/* 5.1. Footer.jsx: Sits below the main content/routes */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
