// src/components/SearchBar.jsx

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import '../styles/SearchBar.css';

/**
 * @component
 * @description The interactive search and filter bar for the content feed.
 */
const SearchBar = () => {
    const [searchText, setSearchText] = useState('');
    const [activeFilter, setActiveFilter] = useState('All');

    // List of quick filters to display
    const filters = ['All', 'Video', 'Event', 'News'];

    // Framer Motion variant for a subtle slide-in animation
    const barVariants = {
        initial: { y: 20, opacity: 0 },
        animate: { 
            y: 0, 
            opacity: 1, 
            transition: { 
                type: 'spring', 
                stiffness: 100, 
                damping: 20, 
                delay: 0.2 
            } 
        },
    };

    const handleSearchChange = (event) => {
        setSearchText(event.target.value);
        console.log(`Searching for: ${event.target.value}`);
    };

    const handleFilterClick = (filter) => {
        setActiveFilter(filter);
        console.log(`Active filter changed to: ${filter}`);
    };

    // Handler for the actual search button click
    const handleSearchSubmit = () => {
        console.log(`Search button clicked for: ${searchText}`);
        // This is where you would trigger the search functionality
    };

    return (
        <motion.div 
            className="search-bar-container"
            variants={barVariants}
            initial="initial"
            animate="animate"
        >
            {/* Main Search Input Group (The large oval shape) */}
            <div className="search-input-group">
                {/* Search Input Field */}
                <input
                    type="text"
                    className="search-input"
                    placeholder={`Search NYC Insight for ${activeFilter} content...`}
                    value={searchText}
                    onChange={handleSearchChange}
                />

                {/* Search Button (The dark, rounded end piece) */}
                <button 
                    className="search-button"
                    onClick={handleSearchSubmit}
                    aria-label="Search"
                >
                    üîç
                </button>
            </div>

            {/* Filter Buttons Group */}
            <div className="filter-group">
                {filters.map(filter => (
                    <button
                        key={filter}
                        className={`filter-button ${activeFilter === filter ? 'active' : ''}`}
                        onClick={() => handleFilterClick(filter)}
                    >
                        {filter}
                    </button>
                ))}
            </div>
        </motion.div>
    );
};

export default SearchBar;
