// src/components/Footer.jsx
import React from 'react';
import '../styles/Footer.css'; 

/**
 * @component
 * @description The application's dark, full-width footer containing branding, 
 * navigation links, and social media placeholders.
 */
const Footer = () => {

    // Reuse the links from the Header for consistency
    const navLinks = [
        { label: 'Home', href: '/' },
        { label: 'Boroughs', href: '/boroughs' },
        { label: 'Categories', href: '/categories' },
        { label: 'Live Feed', href: '/feed' },
        { label: 'Dashboard', href: '/dashboard' },
    ];

    // Placeholder social links (In a real app, use Font Awesome icons)
    const socialLinks = [
        { name: 'Twitter', icon: 'üê¶', href: '#' },
        { name: 'Instagram', icon: 'üì∏', href: '#' },
        { name: 'YouTube', icon: '‚ñ∂Ô∏è', href: '#' },
    ];

    return (
        <footer className="footer">
            <div className="footerContent page-container">

                {/* 1. Left: Branding & Copyright */}
                <div className="footer-branding">
                    <div className="footer-logo">
                        <a href="/">
                            NYC **Insight**
                        </a>
                    </div>
                    <p className="footer-copyright">
                        &copy; {new Date().getFullYear()} NYC Insight.<br/>
                        All rights reserved. Data sourced from public feeds.
                    </p>
                </div>

                {/* 2. Center: Navigation Links */}
                <div className="footer-nav">
                    <h4>Navigation</h4>
                    <ul className="footer-nav-list">
                        {navLinks.map((link) => (
                            <li key={link.label}>
                                <a href={link.href}>{link.label}</a>
                            </li>
                        ))}
                    </ul>
                </div>

                {/* 3. Right: Social Media */}
                <div className="footer-social">
                    <h4>Connect</h4>
                    <div className="social-icons">
                        {socialLinks.map((social) => (
                            <a 
                                key={social.name} 
                                href={social.href} 
                                target="_blank" 
                                rel="noopener noreferrer" 
                                className="social-icon"
                                title={social.name}
                            >
                                {social.icon}
                            </a>
                        ))}
                    </div>
                </div>

            </div>
        </footer>
    );
};

export default Footer;
