// src/components/HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import CTAButton from './CTAButton';
import '../styles/HeroSection.css';

/**
 * @component
 * @description The main, large headline and call-to-action area on the landing page.
 */
const HeroSection = () => {

    // Framer Motion variant for text and buttons fade-up
    const contentVariants = {
        hidden: { y: 20, opacity: 0 },
        visible: { 
            y: 0, 
            opacity: 1, 
            transition: { 
                type: 'spring', 
                stiffness: 100, 
                damping: 20, 
                duration: 0.5 
            } 
        },
    };

    return (
        <div className="hero-section">
            <motion.div
                initial="hidden"
                animate="visible"
                variants={{
                    visible: { transition: { staggerChildren: 0.2 } }
                }}
                style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}
            >
                <motion.h1 
                    className="hero-title"
                    variants={contentVariants}
                >
                    The City That Doesn’t Sleep!!
                </motion.h1>

                <motion.p 
                    className="hero-subtitle"
                    variants={contentVariants}
                >
                    “Real-time stories, videos, alerts, and insights across all five boroughs — all in one place.”
                </motion.p>

                <motion.div 
                    className="hero-actions"
                    variants={contentVariants}
                >
                    {/* Primary Action Button */}
                    <CTAButton 
                        label="Explore Now" 
                        variant="primary"
                        onClick={() => console.log('Hero CTA: Explore Now')}
                    />

                    {/* Secondary Action Button */}
                    <CTAButton 
                        label="View Latest" 
                        variant="outline-white" 
                        onClick={() => console.log('Hero CTA: View Latest')}
                    />
                </motion.div>
            </motion.div>
        </div>
    );
};

export default HeroSection;
