// src/components/FeaturesSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import '../styles/FeaturesSection.css';

/**
 * @component
 * @description Highlights the core value propositions and features of the NYC Insight platform.
 */

// FIX: Updated featureData to use class names instead of emojis
const featuresData = [
    {
        iconClass: 'icon-realtime', 
        title: 'Real-Time Insights',
        description: 'Get breaking news, live event alerts, and video feeds instantly. We aggregate the city for you, as it happens.',
    },
    {
        iconClass: 'icon-diverse', 
        title: 'All Content Types',
        description: 'From deep-dive articles and government alerts to trending YouTube videos and local event announcements—it’s all here.',
    },
    {
        iconClass: 'icon-local', 
        title: 'Hyper-Local Focus',
        description: 'Filter content by Borough (Brooklyn, Queens, etc.) or Category to drill down to the stories that matter most to your neighborhood.',
    },
];

// Framer Motion variant for each card (staggered fade-up)
const cardVariants = {
    initial: { y: 30, opacity: 0 },
    animate: { 
        y: 0, 
        opacity: 1, 
        transition: {
            duration: 0.5,
            ease: "easeOut"
        }
    }
};

const FeaturesSection = () => {
    return (
        <div className="features-section-container page-container">

            <h2 className="features-headline">
                What's the Hype About?
            </h2>
            <p className="features-subtext">
                NYC Insight is your single source for the city that never sleeps. We cut through the noise to bring you the stories, events, and videos you care about most.
            </p>

            <motion.div 
                className="features-grid"
                initial="initial"
                whileInView="animate" 
                viewport={{ once: true, amount: 0.2 }}
                transition={{ staggerChildren: 0.15 }}
            >
                {featuresData.map((feature, index) => (
                    <motion.div 
                        key={index} 
                        className="feature-card"
                        variants={cardVariants}
                    >
                        <div className={`feature-icon ${feature.iconClass}`}></div>
                        <h3 className="feature-title">{feature.title}</h3>
                        <p className="feature-description">{feature.description}</p>
                    </motion.div>
                ))}
            </motion.div>
        </div>
    );
};

export default FeaturesSection;
