// src/components/Footer.jsx
import React from 'react';
import '../styles/Footer.css';

const NAV_LINKS = [
  { label: 'Home', href: '/' },
  { label: 'Boroughs', href: '/borough/manhattan' }, // Default to Manhattan as an example
  { label: 'Categories', href: '/breaking-news' },   // Or you could make a dropdown if needed
  { label: 'Live Feed', href: '/feed' },
  { label: 'Dashboard', href: '/dashboard' },
];

const SOCIAL_LINKS = [
  { name: 'Twitter', iconClass: 'icon-twitter', href: 'https://twitter.com' },
  { name: 'Instagram', iconClass: 'icon-instagram', href: 'https://instagram.com' },
  { name: 'YouTube', iconClass: 'icon-youtube', href: 'https://youtube.com' },
];

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <footer className="footer">
      <div className="footerContent page-container">
        {/* Branding & Copyright */}
        <div className="footer-branding">
          <div className="footer-logo">
            <a href="/">
              <span className="footer-logo-icon" aria-hidden="true"></span>
              NYC <strong>Insight</strong>
            </a>
          </div>
          <p className="footer-copyright">
            &copy; {currentYear} NYC Insight.<br/>
            All rights reserved. Data sourced from public feeds.
          </p>
        </div>

        {/* Navigation Links */}
        <div className="footer-nav">
          <h4 className="footer-heading">Navigation</h4>
          <ul className="footer-nav-list">
            {NAV_LINKS.map((link) => (
              <li key={link.label}>
                <a href={link.href}>{link.label}</a>
              </li>
            ))}
          </ul>
        </div>

        {/* Social Media */}
        <div className="footer-social">
          <h4 className="footer-heading">Connect</h4>
          <div className="social-icons">
            {SOCIAL_LINKS.map((social) => (
              <a 
                key={social.name} 
                href={social.href} 
                target="_blank" 
                rel="noopener noreferrer" 
                className={`social-icon ${social.iconClass}`}
                title={social.name}
                aria-label={social.name}
              >
                <span className="sr-only">{social.name}</span>
              </a>
            ))}
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
