// src/components/DropdownMenu.jsx
import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/DropdownMenu.css'; 

/**
 * @component
 * @description Modern, animated dropdown menu for navigation (e.g., Boroughs, Categories).
 */
const DropdownMenu = ({ label, options, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef(null);

  // Framer Motion variant for the menu (slide and fade)
  const menuVariants = {
    initial: { opacity: 0, height: 0, y: -10 },
    animate: { 
      opacity: 1, 
      height: 'auto', 
      y: 0, 
      transition: { 
        type: 'tween', 
        duration: 0.2 
      } 
    },
    exit: { 
      opacity: 0, 
      height: 0, 
      y: -10, 
      transition: { 
        type: 'tween', 
        duration: 0.2 
      } 
    },
  };

  // Logic: Click-outside close
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (containerRef.current && !containerRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [containerRef]);

  const handleSelect = (option) => {
    onSelect(option);
    setIsOpen(false); // Close menu on selection
  };

  return (
    <div className="dropdown-container" ref={containerRef}>

      {/* Dropdown Header: Click to toggle menu */}
      <div 
        className="dropdown-header" 
        onClick={() => setIsOpen(!isOpen)}
        aria-expanded={isOpen}
      >
        {label}

        {/* FIX 2: Corrected animation logic. Rotates from 90deg (down) to 270deg (up). */}
        <motion.span 
          className="dropdown-arrow"
          // If open, rotate to 270deg (which looks like an up arrow)
          // If closed, return to 90deg (which looks like a down arrow)
          animate={{ rotate: isOpen ? 270 : 90 }} 
          transition={{ duration: 0.2 }}
        >
          &gt; 
        </motion.span>
      </div>

      {/* Dropdown Menu Content: Slide and Fade (Framer Motion) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            key="dropdown-menu"
            className="dropdown-menu"
            variants={menuVariants}
            initial="initial"
            animate="animate"
            exit="exit"
          >
            {options.map((option, index) => (
              <div 
                key={index}
                className="dropdown-item"
                onClick={() => handleSelect(option)}
              >
                {option}
              </div>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default DropdownMenu;
