// src/components/Header.jsx
import React, { useState } from 'react';
import { motion, useScroll, useMotionValueEvent } from 'framer-motion';
import DropdownMenu from './DropdownMenu'; 
import CTAButton from './CTAButton'; 
import '../styles/Header.css';

/**
 * @component
 * @description Sticky header with logo, navigation, and CTA button.
 */
const Header = () => {
    const [headerStyle, setHeaderStyle] = useState({});
    const { scrollY } = useScroll();

    // Framer Motion hook to track scroll position and update state
    useMotionValueEvent(scrollY, "change", (latest) => {
        // Apply solid background and shadow after scrolling past 50px
        if (latest > 50) {
            setHeaderStyle({
                backgroundColor: 'rgba(255, 255, 255, 0.98)', // Near opaque white
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
                backdropFilter: 'blur(5px)', // Subtle blur effect for a modern look
            });
        } else {
            // Revert to transparent/subtle style at the top
            setHeaderStyle({});
        }
    });

    const handleDropdownSelect = (option) => {
        console.log(`Dropdown selected: ${option}`);
        // This is where you would implement navigation logic (e.g., using useNavigate)
    };

    // Placeholder data for dropdown menus
    const boroughOptions = ['Manhattan', 'Brooklyn', 'Queens', 'Bronx', 'Staten Island'];
    const categoryOptions = ['Transportation', 'Government', 'Real Estate', 'Culture', 'Crime'];

    return (
        <motion.header 
            className="header" 
            style={headerStyle} // Apply dynamic styles
        >
            <div className="headerContent page-container">

                {/* Logo / Branding */}
                <div className="header-logo">
                    <a href="/">
                        {/* THE ICON PLACEHOLDER */}
                        <span className="header-logo-icon" aria-hidden="true"></span>
                        NYC <strong>Insight</strong>
                    </a>
                </div>

                {/* Main Navigation - Hidden on Mobile */}
                <nav className="header-nav">
                    <DropdownMenu 
                        label="Boroughs" 
                        options={boroughOptions} 
                        onSelect={handleDropdownSelect}
                    />
                    <DropdownMenu 
                        label="Categories" 
                        options={categoryOptions} 
                        onSelect={handleDropdownSelect}
                    />
                    <a href="/feed">Live Feed</a>
                </nav>

                {/* Call To Action Button - Hidden on Mobile */}
                <CTAButton 
                    label="Get App" 
                    variant="secondary" 
                    onClick={() => console.log('CTA: Get App')}
                />
            </div>
        </motion.header>
    );
};

export default Header;
