// src/components/VideoModal.jsx
import React, { useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/VideoModal.css';

const VideoModal = ({ video, onClose }) => {
  useEffect(() => {
    // Prevent body scroll when modal is open
    document.body.style.overflow = 'hidden';

    // Handle Escape key to close modal
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', handleEscape);

    return () => {
      document.body.style.overflow = 'unset';
      document.removeEventListener('keydown', handleEscape);
    };
  }, [onClose]);

  if (!video) return null;

  const getEmbedUrl = () => {
    if (video.source === 'YouTube') {
      const videoId = video.id || video.link.split('v=')[1]?.split('&')[0];
      return `https://www.youtube.com/embed/${videoId}?autoplay=1`;
    }
    if (video.source === 'TikTok') {
      // TikTok embed
      return video.link;
    }
    return null;
  };

  const embedUrl = getEmbedUrl();

  return (
    <AnimatePresence>
      <motion.div 
        className="video-modal-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        onClick={onClose}
      >
        <motion.div 
          className="video-modal-content"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
          onClick={(e) => e.stopPropagation()}
        >
          <button className="modal-close-button" onClick={onClose}>
            ✕
          </button>

          <div className="modal-video-container">
            {video.source === 'YouTube' && embedUrl && (
              <iframe
                src={embedUrl}
                title={video.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              />
            )}
            {video.source === 'TikTok' && (
              <div className="tiktok-notice">
                <p>TikTok videos can't be embedded.</p>
                <a 
                  href={video.link} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="external-link-button"
                >
                  Watch on TikTok →
                </a>
              </div>
            )}
          </div>

          <div className="modal-video-info">
            <h3>{video.title}</h3>
            <p className="modal-source">{video.source}</p>
            <p className="modal-description">{video.summary}</p>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default VideoModal;
