// src/components/Header.jsx
import React from 'react';
import { motion } from 'framer-motion';
import '../styles/Header.css'; // <-- Uses standard CSS import
import CTAButton from './CTAButton';
import DropdownMenu from './DropdownMenu'; // Placeholder for the next step

/**
 * @component
 * @description The main navigation header with logo, center links, and glossy Auth buttons.
 * Features: Sticky, Framer Motion fade-down, subtle bottom shadow.
 */
const Header = () => {
  // Define the main navigation links
  const navLinks = [
    { label: 'Home', href: '/' },
    // These will be rendered as DropdownMenu components later
    { label: 'Boroughs', isDropdown: true, options: ['Manhattan', 'Brooklyn', 'Queens', 'Bronx', 'Staten Island'] },
    { label: 'Categories', isDropdown: true, options: ['Videos', 'Breaking News', 'Events'] },
    { label: 'Live Feed', href: '/feed' },
    { label: 'Dashboard', href: '/dashboard' },
  ];

  // Framer Motion variant for the initial fade-down animation (9. Animation Ideas)
  const headerVariants = {
    initial: { y: -100, opacity: 0 },
    animate: { 
      y: 0, 
      opacity: 1, 
      transition: { 
        type: 'spring', 
        stiffness: 70, 
        damping: 15,
        delay: 0.1 
      } 
    },
  };

  const handleDropdownSelect = (option) => {
    console.log(`Selected: ${option}`);
    // In a full implementation, this would navigate or filter
  };

  return (
    <motion.header 
      className="header" // <-- Class name used directly from Header.css
      variants={headerVariants} 
      initial="initial" 
      animate="animate"
    >
      <div className="headerContent page-container">

        {/* Left: Logo / Text-brand “NYC Insight” */}
        <div className="logo">
          <a href="/">
            NYC **Insight**
          </a>
        </div>

        {/* Center: Main Navigation */}
        <nav className="navCenter">
          {navLinks.map((link) => (
            <React.Fragment key={link.label}>
              {link.isDropdown ? (
                // Placeholder for DropdownMenu (1.3. DropdownMenu.jsx)
                <DropdownMenu
                  label={link.label}
                  options={link.options}
                  onSelect={handleDropdownSelect}
                  // This component will be built next
                />
              ) : (
                <a href={link.href} className="navLink">
                  {link.label}
                </a>
              )}
            </React.Fragment>
          ))}
        </nav>

        {/* Right: Glossy Auth buttons */}
        <div className="authButtons">
          <CTAButton label="Login" variant="outline" onClick={() => console.log('Go to Login')} />
          <CTAButton label="Sign Up" variant="outline" onClick={() => console.log('Go to Sign Up')} />
        </div>

        {/* Mobile Burger Menu Placeholder (Hidden on Desktop) */}
        <div className="mobileMenu">
            {/* Burger Menu Icon will be here */}
            ☰
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
