// src/components/HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import CTAButton from './CTAButton';
import '../styles/HeroSection.css';

/**
 * @component
 * @description The main, large headline and call-to-action area on the landing page.
 * FIX: Removed text animations to guarantee visibility.
 */
const HeroSection = () => {

    // Only keeping animation for the buttons as they seem to work fine
    const buttonVariants = {
        initial: { y: 20, opacity: 0 },
        animate: { 
            y: 0, 
            opacity: 1, 
            transition: { duration: 0.5 } 
        },
    };

    return (
        <div className="hero-section">
            <div style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', position: 'relative', zIndex: 2 }}>

                {/* FIX: Standard H1 with inline style to force visibility */}
                <h1 
                    className="hero-title"
                    style={{ opacity: 1, visibility: 'visible', color: '#FFFFFF' }}
                >
                    The City That Doesn’t Sleep!!
                </h1>

                {/* FIX: Standard P tag with inline style to force visibility */}
                <p 
                    className="hero-subtitle"
                    style={{ opacity: 1, visibility: 'visible', color: '#FFFFFF' }}
                >
                    “Real-time stories, videos, alerts, and insights across all five boroughs — all in one place.”
                </p>

                {/* We keep the button animation since buttons were visible in your screenshot */}
                <motion.div 
                    className="hero-actions"
                    variants={buttonVariants}
                    initial="initial"
                    animate="animate"
                >
                    <CTAButton 
                        label="Explore Now" 
                        variant="primary"
                        onClick={() => console.log('Hero CTA: Explore Now')}
                    />

                    <CTAButton 
                        label="View Latest" 
                        variant="outline-white" 
                        onClick={() => console.log('Hero CTA: View Latest')}
                    />
                </motion.div>
            </div>
        </div>
    );
};

export default HeroSection;
