// src/components/ContentFeed.jsx
import React from 'react';
import NewsCard from './NewsCard';
import '../styles/NewsCard.css'; // Contains the grid styles

/**
 * @typedef {object} ContentItem
 * @property {number} id - Unique identifier.
 * @property {string} title - The title of the article/video.
 * @property {string} summary - A short summary or description.
 * @property {string} source - The platform source (e.g., 'YouTube', 'Blog', 'Event').
 * @property {string} mediaUrl - URL for the thumbnail image.
 * @property {string} link - The full link to the source content.
 */

/**
 * @type {ContentItem[]}
 * @description Static data used to demonstrate the functionality and design of the NewsCard.
 */
const DUMMY_CONTENT = [
    {
        id: 1,
        title: "Brooklyn Bridge Park Expansion Plans: New Pier Revealed",
        summary: "The city council approved a multi-million dollar proposal to add recreational space and protected wetlands to the Brooklyn waterfront.",
        source: "NYC Blog",
        mediaUrl: "https://images.unsplash.com/photo-1577908906566-0d4536691459?fit=crop&w=600&q=80",
        link: "#"
    },
    {
        id: 2,
        title: "Subway Ridership Hits Pre-Pandemic Highs on A and 6 Lines",
        summary: "An analysis of MTA data shows the two lines leading the recovery, prompting talks of increased service during peak hours across Manhattan.",
        source: "NYTimes",
        mediaUrl: "https://images.unsplash.com/photo-1594950949246-d8d1f2746401?fit=crop&w=600&q=80",
        link: "#"
    },
    {
        id: 3,
        title: "Top 5 Places to Get a Classic NY Slice in Queens",
        summary: "From Astoria to Flushing, we review the best classic pizzerias that define the borough's culinary scene.",
        source: "Food Blog",
        mediaUrl: "https://images.unsplash.com/photo-1563770732871-3f740a1b6c07?fit=crop&w=600&q=80",
        link: "#"
    },
    {
        id: 4,
        title: "Video: Exploring the Hidden Architecture of the Financial District",
        summary: "A deep dive into the historical buildings and modern skyscrapers of Lower Manhattan, highlighting key design elements.",
        source: "YouTube",
        mediaUrl: "https://images.unsplash.com/photo-1542171221-cf7b98751f8a?fit=crop&w=600&q=80",
        link: "#"
    },
    {
        id: 5,
        title: "The Bronx Zoo's New Exhibit: The World of Lizards",
        summary: "Opening this summer, the new permanent exhibit features rare and exotic species from around the globe.",
        source: "Zoo Event",
        mediaUrl: "https://images.unsplash.com/photo-1561725838-895781cc3f44?fit=crop&w=600&q=80",
        link: "#"
    },
    {
        id: 6,
        title: "Staten Island Ferry Terminal Redevelopment: Public Meeting Held",
        summary: "Local officials discussed the updated plans for commercial space and traffic flow improvements at the terminal.",
        source: "City Gov",
        mediaUrl: "https://images.unsplash.com/photo-1620248492027-2c9c7f96b79c?fit=crop&w=600&q=80",
        link: "#"
    }
];

/**
 * @component
 * @description Container for the main content feed, responsible for grid layout and data mapping.
 */
const ContentFeed = () => {
    return (
        <div className="content-feed-container">
            {/* The feed grid wrapper */}
            <div className="feed-grid">
                {DUMMY_CONTENT.map(content => (
                    // NewsCard renders with its own animation (whileInView)
                    <NewsCard key={content.id} content={content} />
                ))}
            </div>
        </div>
    );
};

export default ContentFeed;
