// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import Header from './components/Header';
import HeroSection from './components/HeroSection'; 
import ContentFeed from './components/ContentFeed'; // <-- NEW IMPORT

/**
 * @component
 * @description The main application wrapper, defining the global layout and routing structure.
 */
function App() {
  return (
    <Router>
      <div className="App">
        {/* 1.1. Header.jsx: Sticky and animated header */}
        <Header /> 

        <main>
          <Routes>
            <Route path="/" element={
              <div className="page-container" style={{ paddingTop: '40px' }}>

                {/* 2.1. HeroSection.jsx - The headline component for the Landing Page */}
                <HeroSection /> 

                <h2 style={{ marginTop: '60px', color: 'var(--color-navy-blue)' }}>
                  Latest NYC Headlines
                </h2>

                {/* 3.1. ContentFeed.jsx - The main grid of news cards */}
                <ContentFeed /> 

                {/* Placeholder content removed:
                <p style={{ marginTop: '20px' }}>
                    Content Feed will be loaded here (Section 3).
                </p>
                <div style={{ height: '150vh', paddingBottom: '100px' }}>
                </div>
                */}

              </div>
            } />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
