// src/components/ContentFeed.jsx
import React, { useState, useEffect } from 'react';
import NewsCard from './NewsCard';
import '../styles/NewsCard.css';
import { fetchAllContent } from '../services/contentService';

const ContentFeed = () => {
  const [content, setContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadContent = async () => {
      try {
        setLoading(true);
        const data = await fetchAllContent({
          query: 'New York',
          includeVideos: true,
          includeArticles: true
        });
        setContent(data);
      } catch (err) {
        setError('Failed to load content. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    loadContent();
  }, []);

  if (loading) {
    return (
      <div className="content-feed-container">
        <div className="feed-loading">
          <div className="spinner"></div>
          <p>Loading NYC content...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="content-feed-container">
        <div className="feed-error">
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="content-feed-container">
      <div className="feed-grid">
        {content.length > 0 ? (
          content.map((item) => (
            <NewsCard key={item.id} content={item} />
          ))
        ) : (
          <p className="feed-empty">No content available at the moment.</p>
        )}
      </div>
    </div>
  );
};

export default ContentFeed;
