// src/components/NewsCard.jsx
import React from 'react';
import { motion } from 'framer-motion';
import '../styles/NewsCard.css';

/**
 * @component
 * @description A modern, glossy card component used to display aggregated content 
 * (News, Videos, Events) from different sources.
 * @param {object} content - The content object containing card data.
 * @param {string} content.title - The title of the article/video.
 * @param {string} content.summary - A short summary or description.
 * @param {string} content.source - The platform source (e.g., 'YouTube', 'Blog', 'Event').
 * @param {string} content.mediaUrl - URL for the thumbnail image.
 * @param {string} content.link - The full link to the source content.
 */

// Framer Motion variant for fade-up on load (Staggered feed entrance)
const cardVariants = {
  initial: { y: 20, opacity: 0 },
  animate: { 
    y: 0, 
    opacity: 1, 
    transition: {
      duration: 0.5,
      ease: "easeOut"
    }
  }
};

const NewsCard = ({ content }) => {
  // Destructure content props
  const { title, summary, source, mediaUrl, link } = content;

  // Function to render the media section based on source type
  const renderMedia = () => {
    // Determine if it's a video/media type that needs an overlay
    const isVideo = source.toLowerCase() === 'youtube' || source.toLowerCase() === 'tiktok';

    return (
      <div className="card-media">
        <img src={mediaUrl} alt={title} className="card-image" />

        {/* Conditional Video Icon Overlay */}
        {isVideo && (
          <div className="video-icon" title={`View on ${source}`}>
            â–¶
          </div>
        )}
      </div>
    );
  };

  return (
    // motion.a is used because the card itself is the link (anchor tag)
    <motion.a 
      href={link} 
      target="_blank" 
      rel="noopener noreferrer"
      className="news-card"
      variants={cardVariants}
      initial="initial"
      whileInView="animate" // Triggers animation when card scrolls into view
      viewport={{ once: true, amount: 0.2 }} // Only animate once, when 20% of element is visible
    >
      {/* 1. Media Section (Image/Video Placeholder) */}
      {renderMedia()}

      {/* 2. Content Section (Source, Title, Summary) */}
      <div className="card-content">

        {/* Source Tag (Top Left) */}
        <div className="card-source-tag">{source}</div>

        {/* Title */}
        <h3 className="card-title">{title}</h3>

        {/* Summary */}
        <p className="card-summary">{summary}</p>

      </div>
    </motion.a>
  );
};

export default NewsCard;
