// src/components/HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import CTAButton from './CTAButton';
import '../styles/HeroSection.css';

/**
 * @component
 * @description The main, large headline and call-to-action area on the landing page.
 */
const HeroSection = () => {

    // Framer Motion variant for text and buttons fade-up
    const contentVariants = {
        initial: { y: 20, opacity: 0 },
        animate: { 
            y: 0, 
            opacity: 1, 
            transition: { 
                type: 'spring', 
                stiffness: 100, 
                damping: 20, 
                delay: 0.3 
            } 
        },
    };

    return (
        <div className="hero-section">
            <motion.h1 
                className="hero-title"
                variants={contentVariants}
                initial="initial"
                animate="animate"
            >
                The City That Doesn't Sleep!!
            </motion.h1>

            <motion.p 
                className="hero-subtitle"
                variants={contentVariants}
                initial="initial"
                animate="animate"
                transition={{ delay: 0.4 }}
            >
                "Real-time stories, videos, alerts, and insights across all five boroughs â€” all in one place."
            </motion.p>

            <motion.div 
                className="hero-actions"
                variants={contentVariants}
                initial="initial"
                animate="animate"
                transition={{ delay: 0.5 }}
            >
                {/* Primary Action Button */}
                <CTAButton 
                    label="Explore Now" 
                    variant="primary"
                    onClick={() => console.log('Hero CTA: Explore Now')}
                />

                {/* Secondary Action Button */}
                <CTAButton 
                    label="View Latest" 
                    variant="outline-white" /* We'll assume a white outline button */
                    onClick={() => console.log('Hero CTA: View Latest')}
                />
            </motion.div>
        </div>
    );
};

export default HeroSection;
