// src/components/HeroSection.jsx
import React from 'react';
import { motion } from 'framer-motion';
import '../styles/HeroSection.css';
import CTAButton from './CTAButton'; // Reusable button from earlier steps

/**
 * @component
 * @description The main landing page hero banner.
 * Features: Glossy card, NYC image, gradient overlay, text slide-up animation, image parallax.
 */

// Framer Motion variants for staggered text reveal
const containerVariants = {
  // Container starts invisible and slightly moved up
  initial: { opacity: 0 },
  // Animates to visible, with children staggering their entry
  animate: { 
    opacity: 1, 
    transition: {
      staggerChildren: 0.2, // Time between each child animation
      delayChildren: 0.3,  // Delay before the first child starts
    }
  }
};

const itemVariants = {
  initial: { y: 50, opacity: 0 },
  // Hero text slide up on load (9. Animation Ideas)
  animate: { 
    y: 0, 
    opacity: 1, 
    transition: {
      duration: 0.6,
      ease: [0.6, 0.05, 0.0, 0.9] // FIX: Corrected invalid cubic-bezier value
    } 
  }
};

const HeroSection = () => {

  // Framer Motion animation for the subtle parallax image drift
  const parallaxAnimation = {
    x: [1, -2, 1], // Oscillate x position
    transition: {
      duration: 15,
      ease: "easeInOut",
      repeat: Infinity,
      repeatType: "reverse",
    }
  };

  return (
    <motion.div className="hero-card">

      {/* Background Image with Slow Drift/Parallax */}
      <motion.div 
        className="hero-background"
        animate={parallaxAnimation} // Apply the slow drift animation
      />

      {/* Navy Gradient Overlay */}
      <div className="hero-overlay" />

      {/* Content Container with Staggered Animation */}
      <motion.div 
        className="hero-content"
        variants={containerVariants}
        initial="initial"
        animate="animate"
      >
        {/* Heading (H2) */}
        <motion.h2 
          className="hero-heading"
          variants={itemVariants}
        >
          The City That Doesn’t Sleep!!
        </motion.h2>

        {/* Subtext */}
        <motion.p 
          className="hero-subtext"
          variants={itemVariants}
        >
          “Real-time stories, videos, alerts, and insights across all five boroughs — all in one place.”
        </motion.p>

        {/* CTA Buttons */}
        <motion.div 
          className="hero-cta-buttons"
          variants={itemVariants}
        >
          {/* Explore Now (primary) - Buttons pulse subtly */}
          <CTAButton 
            label="Explore Now" 
            variant="primary" 
            onClick={() => console.log('Navigating to Feed')}
            // Subtly pulse the primary button infinitely
            animate={{ scale: [1, 1.02, 1] }} 
            transition={{ duration: 2, repeat: Infinity, repeatType: "reverse" }}
          />

          {/* View Latest (outline glossy) */}
          <CTAButton 
            label="View Latest" 
            variant="outline" 
            onClick={() => console.log('Navigating to Latest')}
          />
        </motion.div>

      </motion.div>
    </motion.div>
  );
};

export default HeroSection;
