// src/components/ContentFeed.jsx
import React, { useState, useEffect } from 'react';
import NewsCard from './NewsCard';
import VideoModal from './VideoModal';
import '../styles/NewsCard.css';
import { fetchAllContent } from '../services/contentService';

const ContentFeed = ({ searchQuery, activeFilter }) => {
  const [content, setContent] = useState([]);
  const [filteredContent, setFilteredContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    const loadContent = async () => {
      try {
        setLoading(true);
        const query = searchQuery || 'New York';
        const data = await fetchAllContent({
          query,
          includeVideos: true,
          includeArticles: true
        });
        setContent(data);
        setFilteredContent(data);
      } catch (err) {
        setError('Failed to load content. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    loadContent();
  }, [searchQuery]);

  useEffect(() => {
    // Filter content based on active filter
    if (activeFilter === 'All') {
      setFilteredContent(content);
    } else if (activeFilter === 'Video') {
      setFilteredContent(content.filter(item => item.type === 'video'));
    } else if (activeFilter === 'Article') {
      setFilteredContent(content.filter(item => item.type === 'article'));
    } else if (activeFilter === 'YouTube') {
      setFilteredContent(content.filter(item => item.source === 'YouTube'));
    } else if (activeFilter === 'TikTok') {
      setFilteredContent(content.filter(item => item.source === 'TikTok'));
    }
  }, [activeFilter, content]);

  const handleVideoClick = (video) => {
    setSelectedVideo(video);
  };

  const handleCloseModal = () => {
    setSelectedVideo(null);
  };

  if (loading) {
    return (
      <div className="content-feed-container">
        <div className="feed-loading">
          <div className="spinner"></div>
          <p>Loading NYC content...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="content-feed-container">
        <div className="feed-error">
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="content-feed-container">
        <div className="feed-grid">
          {filteredContent.length > 0 ? (
            filteredContent.map((item) => (
              <NewsCard 
                key={item.id} 
                content={item}
                onVideoClick={handleVideoClick}
              />
            ))
          ) : (
            <p className="feed-empty">No content available for this filter.</p>
          )}
        </div>
      </div>

      {selectedVideo && (
        <VideoModal 
          video={selectedVideo} 
          onClose={handleCloseModal} 
        />
      )}
    </>
  );
};

export default ContentFeed;
